syntax = "proto3";

package flight;

import "google/protobuf/empty.proto";

message FlightOffersSearch {
  string originLocationCode = 1;
  string destinationLocationCode = 2;
  string departureDate = 3;
  string returnDate = 4;
  int64 adults = 5;
  int64 children = 6;
  int64 infants = 7;
  string travelClass = 8;
}
message FlightOffersRequest {
  FlightOffersSearch flightOffersSearch = 1;
}

message FlightOffersResponse {
  string flightOffers = 1;
}

message Flight {
  string id = 1;
  string itineraries = 2;
  string price = 3;
  int64 createdAt = 5;
  int64 updateAt = 6;
}


message FlightCreate {
  string userId = 1;
  string itineraries = 2;
  string price = 3;
  bool isPackage = 4;
}

message FlightCreateRequest {
  FlightCreate flightCreate = 1;
}

message FlightResponse {
  Flight flight = 1;
}

message FlightListResponse {
  repeated Flight flight = 1;
}


message FlightShowRequest {
  string id = 1;
}

message FlightByUserRequest {
  string userId = 1;
}

service Flights {
  rpc searchFlightOffer(FlightOffersRequest) returns (FlightOffersResponse);
  rpc createFlight(FlightCreateRequest) returns (FlightResponse);
  rpc listFlights(google.protobuf.Empty) returns (FlightListResponse);
  rpc showFlight(FlightShowRequest) returns (FlightResponse);
  rpc listFlightbyUser(FlightByUserRequest) returns (FlightListResponse);
}

syntax = "proto3";

package hotel;

import "google/protobuf/empty.proto";

message HotelOffersSearch {
  string cityCode = 1;
  string checkInDate = 2;
  string checkOutDate = 3;
  int64 roomQuantity = 4;
  int64 adults = 5;
}

message HotelOffersRequest {
  HotelOffersSearch hotelOffersSearch = 1;
}

message HotelOffersResponse {
  string hotelOffers = 1;
}

message Hotel {
  string id = 1;
  string hotel = 2;
  string offers = 3;
  int64 createdAt = 5;
  int64 updateAt = 6;
}


message HotelCreate {
  string userId = 1;
  string hotel = 2;
  string offers = 3;
  bool isPackage = 4;

}

message HotelCreateRequest {
  HotelCreate hotelCreate = 1;
}

message HotelResponse {
  Hotel hotel = 1;
}

message HotelListResponse {
  repeated Hotel hotel = 1;
}


message HotelShowRequest {
  string id = 1;
}

message HotelsByUserRequest {
  string userId = 1;
}

service Hotels {
  rpc searchHotelOffer(HotelOffersRequest) returns (HotelOffersResponse);
  rpc createHotel(HotelCreateRequest) returns (HotelResponse);
  rpc listHotels(google.protobuf.Empty) returns (HotelListResponse);
  rpc showHotel(HotelShowRequest) returns (HotelResponse);

  rpc listHotelByUser(HotelsByUserRequest) returns (HotelListResponse);
}

syntax = "proto3";

package packagetrip;

import "google/protobuf/empty.proto";

message PackageSearch {
  string originLocationCode = 1;
  string destinationLocationCode = 2;
  string departureDate = 3;
  string returnDate = 4;
  int64 adults = 5;
  int64 children = 6;
  int64 infants = 7;
  string travelClass = 8;
  int64 roomQuantity = 9;
}

message PackageSearchResponse {
  string package = 1;

}

message PackageSearchRequest {
  PackageSearch packageSearch = 1;
}

message Flight {
  string intinerantes = 1;
  string price = 2;
}

message Hotel {
  string hotel = 1;
  string offers = 2;
}

message PackageCreate {
  string userId = 1;
  Flight flight = 2;
  Hotel hotel = 3;
  float amount = 4;
  float off = 5;
}

message PackageCreateRequest {
  PackageCreate packageCreate = 1;
}

message PackageResponse {
  string id = 1;
  Flight flight = 2;
  Hotel hotel = 3;
  float amount = 4;
  float off = 5;
  int64 updatedAt = 6;
  int64 createdAt = 7;
}

message PackagesResponse {
  repeated PackageResponse packageResponse = 1;
}

message PackagesByUserRequest {
  string userId = 1;
}

service Packages {
  rpc searchPackage(PackageSearchRequest) returns (PackageSearchResponse);
  rpc createPackage(PackageCreateRequest) returns (PackagesResponse);
  rpc listPackages(google.protobuf.Empty) returns (PackagesResponse);
  // rpc showPackage(PackageRequest) returns (PackagesResponse);
  // rpc updatePackage(PackageRequest) returns (PackagesResponse);

  rpc listPackageByUser(PackagesByUserRequest) returns (PackagesResponse);
}

syntax = "proto3";

package user;

import "google/protobuf/empty.proto";

message User {
  string id = 1;         
  string email = 2;      
  string firstName = 3; 
  string lastName = 4;  
  int64 age = 5;        
  string birthday = 6;   
  int64 updatedAt = 7;
  int64 createdAt = 8;
}

message UserResponse {
  repeated User user = 1;

}

message UserRequest {
  User user = 1;
}

service Users {
  rpc createUser(UserRequest) returns (UserResponse);
  rpc listUsers(google.protobuf.Empty) returns (UserResponse);
  rpc showUser(UserRequest) returns (UserResponse);
  rpc updateUser(UserRequest) returns (UserResponse);
}
